es=keysentences_extraction(text)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    print(keysentences)&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
